package workWechatRobot

import (
	"os"
	"testing"
)

var robot = Robot{Key: os.Getenv("KEY")}
func TestRobot_SendText(t *testing.T) {
	res, _ := robot.SendText("文本发送测试！")
	if res.ErrorCode!=0 {
		t.Fatal(res.ErrorMessage)
	}
}

func TestRobot_SendMarkdown(t *testing.T) {
	res, _ := robot.SendMarkdown(
		`
# 这是一个标题
> 类型: Markdown
> 作者: 远浅
> 来源: 1.1.1.1
`)
	if res.ErrorCode!=0 {
		t.Fatal(res.ErrorMessage)
	}
}

func TestRobot_SendImage(t *testing.T) {
	res, _ := robot.SendImage(
		"",
		"539fe71a3b2b0f7302296653575d8722",
	)
	if res.ErrorCode!=0 {
		t.Fatal(res.ErrorMessage)
	}
}

func TestRobot_SendNews(t *testing.T) {
	ns := []*NewsItem{
		{
			Title:       "卡片消息标题",
			Description: "卡片消息描述",
			URL:         "https://happysooner.com",
			Picurl:      "http://res.mail.qq.com/node/ww/wwopenmng/images/independent/doc/test_pic_msg1.png",
		},
	}
	res, _ := robot.SendNews(ns)
	if res.ErrorCode!=0 {
		t.Fatal(res.ErrorMessage)
	}
}
